mapper.select:
select count(rew_num)
from review_tbl
where pro_num=#{pro_num}

select rew_num, mbs_id, pro_num, rew_contents, rew_score, rew_regdate
from(select rew_num, mbs_id, pro_num, rew_contents, rew_score, rew_regdate,
	  		row_number() over(order by rew_num desc) p_seq
	 from review_tbl
	 where pro_num=#{pro_num})
where p_seq between #{cri.rowStart} and #{cri.rowEnd}

mapper.update:
update review_tbl
set rew_contents=#{rew_contents}, rew_score=#{rew_score}
where rew_num=#{rew_num}
